import React, { useState } from 'react';
import { View, Text, Button } from 'react-native';
import { ButtonGroup } from 'react-native-elements';

const Question = ({ route, navigation }) => {
  const { data } = route.params;

  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [userAnswers, setUserAnswers] = useState([]);

  const handleNextQuestion = () => {
    if (currentIndex < data.length - 1) {
      setUserAnswers([...userAnswers, selectedAnswer]);
      setCurrentIndex(currentIndex + 1);
      setSelectedAnswer(null);
    } else {
      setUserAnswers([...userAnswers, selectedAnswer]);
      navigation.navigate('Summary', { data, userAnswers });
    }
  };

  const handleAnswerSelection = (index) => {
    console.log("Selected answer index:", index);
    if (data[currentIndex].type === "multiple-answer") {
      // Clone the current selected answers array
      const newSelectedAnswers = [...(selectedAnswer || [])];
  
      // Check if the selected answer is already in the array
      const selectedIndex = newSelectedAnswers.indexOf(index);
  
      // If the selected answer is not in the array, add it
      if (selectedIndex === -1) {
        newSelectedAnswers.push(index);
      } else { // If the selected answer is in the array, remove it
        newSelectedAnswers.splice(selectedIndex, 1);
      }
  
      // Update the selected answers state
      setSelectedAnswer(newSelectedAnswers.length > 0 ? newSelectedAnswers : null);
    } else {
      // For "multiple-choice" type questions, simply set the selected answer index
      setSelectedAnswer(index);
    }
  };

  const { prompt, type, choices } = data[currentIndex];

  return (
    <View>
      <Text>{prompt}</Text>
      <ButtonGroup
        onPress={handleAnswerSelection}
        selectedIndex={selectedAnswer}
        buttons={choices}
        containerStyle={{ height: 100 }}
        testID="choices"
      />
      <Button title="Next Question" onPress={handleNextQuestion} testID="next-question" />
    </View>
  );
};

export default Question;
