import './App.css';
import { useCallback, useState } from 'react';
import DurationExercise from './components/DurationExercise';
import RepetitionExercise from './components/RepetitionExercise';
import RunningExercise from './components/RunningExercise';


import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

const MENU_SCREEN = "menu";
const EXERCISE_SCREEN = "exercise";
const DURATION_EXERCISE = "duration";
const REPETITION_EXERCISE = "repetition";
const LAPS_EXERCISE = "laps"


let exerciseList = [
  {type: DURATION_EXERCISE, name: "Running"},
  {type: DURATION_EXERCISE, name: "Rowing"},
  {type: DURATION_EXERCISE, name: "Swimming"},
  {type: REPETITION_EXERCISE, name: "Push Ups"},
  {type: LAPS_EXERCISE, name: "Running with Laps"}
]

const Stack = createNativeStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
    <Stack.Navigator initialRouteName="Home">
      <Stack.Screen name="Home" component={Home} />
      <Stack.Screen name="Duration" component={DurationExercise} />
      <Stack.Screen name="Repetition" component={RepetitionExercise} />
      <Stack.Screen name="Running" component={RunningExercise} />
    </Stack.Navigator>
  </NavigationContainer>
  );
}

const Home = ({ navigation }) => {
  const exercises = [
    { type: 'Duration', name: 'Duration Exercise 1' },
    { type: 'Duration', name: 'Duration Exercise 2' },
    { type: 'Duration', name: 'Duration Exercise 3' },
    { type: 'Repetition', name: 'Repetition Exercise 1' },
    { type: 'Repetition', name: 'Repetition Exercise 2' },
    { type: 'Running', name: 'Running Exercise 1' },
    { type: 'Running', name: 'Running Exercise 2' },
  ];

  const renderItem = ({ item }) => (
    <Button
      title={item.name}
      onPress={() => navigation.navigate(item.type, { exerciseName: item.name })}
    />
  );

  const renderHeader = ({ section: { title } }) => (
    <Text style={styles.sectionHeader}>{title}</Text>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={exercises}
        renderItem={renderItem}
        keyExtractor={(item, index) => index.toString()}
        ListHeaderComponent={<Text style={styles.header}>Choose an Exercise</Text>}
        ListFooterComponent={<View style={styles.footer}></View>}
        ItemSeparatorComponent={() => <View style={styles.separator}></View>}
      />
    </View>
  );
};
