    <View style={styles.container}>
      <Text>{exercise}</Text>
      <Text style={styles.count}>{count}</Text>
      <Button title="Increment" onPress={() => setCount(count => count + 1)} />
      <Button title="Reset" onPress={() => setCount(0)} />
