import React from 'react';
import { View, Text, Button, FlatList, StyleSheet } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

const Stack = createNativeStackNavigator();

const HomeScreen = ({ navigation }) => {
  const exercises = [
    { type: 'Duration', name: 'Duration Exercise 1' },
    { type: 'Duration', name: 'Duration Exercise 2' },
    { type: 'Duration', name: 'Duration Exercise 3' },
    { type: 'Repetition', name: 'Repetition Exercise 1' },
    { type: 'Repetition', name: 'Repetition Exercise 2' },
    { type: 'Running', name: 'Running Exercise 1' },
    { type: 'Running', name: 'Running Exercise 2' },
  ];

  const renderItem = ({ item }) => (
    <Button
      title={item.name}
      onPress={() => navigation.navigate(item.type, { exerciseName: item.name })}
    />
  );

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Choose an Exercise</Text>
      <FlatList
        data={exercises}
        renderItem={renderItem}
        keyExtractor={(item, index) => index.toString()}
        ItemSeparatorComponent={() => <View style={styles.separator} />}
      />
    </View>
  );
};

const App = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Home" component={HomeScreen} />
        {/* Add additional screens here */}
      </Stack.Navigator>
    </NavigationContainer>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  header: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  separator: {
    height: 1,
    backgroundColor: '#ccc',
    marginVertical: 5,
  },
});

export default App;
