import React from 'react';
import { View, Text, Button, FlatList, StyleSheet } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const HomeScreen = () => {
  const navigation = useNavigation();

  const exercises = [
    { type: 'Repetition', name: 'Push Ups' },
    { type: 'Repetition', name: 'Squats' },
    { type: 'Duration', name: 'Running' },
    { type: 'Duration', name: 'Swimming' },
  ];

  const renderItem = ({ item }) => (
    <Button
      title={item.name}
      onPress={() => navigation.navigate('StopWatch', { name: item.name })}
    />
  );

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Choose an Exercise</Text>
      <FlatList
        data={exercises}
        renderItem={renderItem}
        keyExtractor={(item, index) => index.toString()}
        ItemSeparatorComponent={() => <View style={styles.separator} />}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  header: {
    marginTop: 30,
    fontSize: 20,
    fontWeight: '500',
    marginBottom: 10,
  },
  separator: {
    height: 1,
    backgroundColor: '#ccc',
    marginVertical: 5,
  },
});

export default HomeScreen;
