import React, { useCallback, useEffect, useState } from 'react';
import { View, Text, Button, StyleSheet } from 'react-native';

export default function StopWatch({ recordLap }) {
    const [running, setRunning] = useState(false);
    const [timer, setTimer] = useState(0);

    const updateTimer = useCallback(() => {
        if (running) {
            setTimer(prevTimer => prevTimer + 10);
        }
    }, [running]);

    useEffect(() => {
        let currentTimer;
        if (running) {
            currentTimer = setInterval(updateTimer, 10);
        }
        return () => clearInterval(currentTimer);
    }, [running, updateTimer]);

    const startStop = () => {
        setRunning(!running);
    };

    const reset = () => {
        setTimer(0);
    };

    const recordLapHandler = () => {
        if (running) {
            recordLap(timer);
        }
    };

    const mins = String(Math.floor((timer / (1000 * 60)) % 60)).padStart(2, "0");
    const secs = String(Math.floor((timer / 1000) % 60)).padStart(2, "0");
    const mills = String((timer % 1000)).padStart(3, "0");

    return (
        <View style={styles.container}>
            <Text style={styles.header}>
                {mins}:{secs}.{mills}
            </Text>
            <View style={styles.buttonContainer}>
                <Button
                    title={running ? "Pause" : "Start"}
                    onPress={startStop}
                    style={{ fontSize: 30 }}
                />
                <Button
                    title="Reset"
                    onPress={reset}
                    style={{ fontSize: 30 }}
                />
                <Button
                    title="Record Time"
                    onPress={recordLapHandler}
                    style={{ fontSize: 30 }}
                />
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    header: {
        fontSize: 50,
        fontWeight: "500"
    },
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    lapContainer: {
        marginTop: 20,
        alignItems: 'center',
    },
    buttonContainer: {
        marginTop: 20,
    },
});
